import './App.css';
import { useState } from "react";
import Axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';

import Swal from 'sweetalert2'


function App() {

  const [nombre,setNombre] = useState("");
  const [cantidad, setCantidad] = useState(0);
  const [proveedor, setProveedor] = useState('');
  const [fecha, setFecha] = useState('');
  const [precio, setPrecio] = useState('0');
  const [id,setId] = useState();

  const [editar,setEditar] = useState(false);

  const [materialesList, setMateriales] = useState([]);

  const add = ()=>{
    Axios.post("http://localhost:3001/create",{
        nombre: nombre,
        cantidad: cantidad,
        proveedor: proveedor,
        fecha: fecha,
        precio: precio
    }).then(()=>{
      getMateriales();
      limpiarCampos();
      Swal.fire({
        title: "<strong>¡Registro éxitoso!</strong>",
        html: "<i>El material <strong>"+nombre+"</strong> ¡Fue registrado con éxito!</i>",
        icon: 'success',
        timer:3000
      })
    }).catch(function(error){
      Swal.fire({
        icon: 'error',
        title: 'Oops...',
        text: JSON.parse(JSON.stringify(error)).message==="Network Error"?"Intente más tarde":JSON.parse(JSON.stringify(error)).message
      })
    });
  }

  const update = ()=>{
    Axios.put("http://localhost:3001/update",{
      id:id,
      nombre:nombre,
      cantidad: cantidad,
      proveedor: proveedor,
      fecha: fecha,
      precio: precio
    }).then(()=>{
      getMateriales();
      limpiarCampos();
      Swal.fire({
        title: "<strong>¡Actualización exitosa!</strong>",
        html: "<i>El material <strong>"+nombre+"</strong> ¡Fue actualizado con éxito!</i>",
        icon: 'success',
        timer:3000
      }).catch(function(error){
        Swal.fire({
          icon: 'error',
          title: 'Oops...',
          text: JSON.parse(JSON.stringify(error)).message==="Network Error"?"Intente más tarde":JSON.parse(JSON.stringify(error)).message
        })
      });
    });
  }

  const deleteMaterial = (val)=>{

    Swal.fire({
      title: 'Confirmar eliminado?',
      html: "<i>Realmente desea eliminar a <strong>"+val.nombre+"</strong>?</i>",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si, eliminarlo!'
    }).then((result) => {
      if (result.isConfirmed) {
        Axios.delete(`http://localhost:3001/delete/${val.id}`).then((res)=>{
          getMateriales();
          limpiarCampos();
          Swal.fire({
            icon: 'success',
            title: val.nombre+' fue eliminado.',
            showConfirmButton: false,
            timer: 2000
          });
        }).catch(function(error){
          Swal.fire({
            icon: 'error',
            title: 'Oops...',
            text: 'No se logró eliminar el material!',
            footer: JSON.parse(JSON.stringify(error)).message==="Network Error"?"Intente más tarde":JSON.parse(JSON.stringify(error)).message
          })
        });

      }
    });


  }

  const limpiarCampos = ()=>{
    setNombre("");
    setCantidad("");
    setProveedor("");
    setFecha("");
    setPrecio("");
    setId("");
    setEditar(false);
  }

    const editarMaterial = (val)=>{
      setEditar(true);

      setNombre(val.nombre);
      setCantidad(val.edad);
      setProveedor(val.cargo);
      setFecha(val.pais);
      setPrecio(val.anios);
      setId(val.id);
    }


  const getMateriales = ()=>{
    Axios.get("http://localhost:3001/").then((response)=>{
      setMateriales(response.data);
    });
  }

  getMateriales();





  return (
    <div className="container">
      <div className="card text-center">
        <div className="card-header">
          GESTIÓN DE MATERIALES
        </div>
        <div className="card-body">
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Nombre:</span>
            <input 
              type="text"
              onChange={(event) => {
                setNombre(event.target.value);
              }}
              className="form-control" 
              value={nombre}
              placeholder="Ingrese el nombre del material" 
              aria-label="Username" 
              aria-describedby="basic-addon1" 
            />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Cantidad:</span>
            <input 
              type="number"
              onChange={(event) => {
                setCantidad(event.target.value);
              }}        
              className="form-control" 
              value={cantidad}
              placeholder="Ingrese la cantidad" 
              aria-label="Username" 
              aria-describedby="basic-addon1" 
            />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Proveedor:</span>
            <input 
              type="text"
              onChange={(event) => {
                setProveedor(event.target.value);
              }}
              className="form-control" 
              value={proveedor}
              placeholder="Ingrese el proveedor" 
              aria-label="Username" 
              aria-describedby="basic-addon1" 
            />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Fecha:</span>
            <input 
              type="text"
              onChange={(event) => {
                setFecha(event.target.value);
              }}
              className="form-control" 
              value={fecha}
              placeholder="Ingrese la fecha de ingreso" 
              aria-label="Username" 
              aria-describedby="basic-addon1" 
            />
          </div>
          <div className="input-group mb-3">
            <span className="input-group-text" id="basic-addon1">Precio:</span>
            <input 
              type="number"
              onChange={(event) => {
                setPrecio(event.target.value);
              }}
              className="form-control" 
              value={precio}
              placeholder="Ingrese el valor" 
              aria-label="Username" 
              aria-describedby="basic-addon1" 
            />
          </div>
        </div>
        <div className="card-footer text-muted">
        {
          editar? 
          <div>
          <button className='btn btn-warning m-2' onClick={update}>Actualizar</button> 
          <button className='btn btn-info m-2' onClick={limpiarCampos}>Cancelar</button>
          </div>
          :<button className='btn btn-success' onClick={add}>Registrar</button>
        }

      </div>
    </div>

    <table className="table table-striped">
        <thead>
        <tr>
            <th scope="col">#</th>
            <th scope="col">Nombre</th>
            <th scope="col">Cantidad</th>
            <th scope="col">Proveedor</th>
            <th scope="col">Fecha</th>
            <th scope="col">Precio</th>
            <th scope="col">Acciones</th>
        </tr>
      </thead>
      <tbody>

      {
          materialesList.map((val,key)=>{
            return <tr key={val.id}>
                    <th>{val.id}</th>
                    <td>{val.nombre}</td>
                    <td>{val.cantidad}</td>
                    <td>{val.proveedor}</td>
                    <td>{val.fecha}</td>
                    <td>{val.precio}</td>
                    <td>
                    <div className="btn-group" role="group" aria-label="Basic example">
                      <button type="button"
                      onClick={()=>{
                        editarMaterial(val);
                      }}
                      className="btn btn-info">Editar</button>
                      <button type="button" onClick={()=>{
                        deleteMaterial(val);
                      }} className="btn btn-danger">Eliminar</button>
                    </div>
                    </td>
                  </tr>

          })
        }


      </tbody>  
    </table>


    </div>
  );
}

export default App;
